 import React, { useEffect, useState } from "react";
 import { useLocation } from "react-router-dom";
 import NewsHome from "./NewsHome";

 const NewsItem = () => {
   const location = useLocation();
   const [data, setData] = useState([]);
   console.log(data);
   useEffect(() => { 
     const fetchData = async () => {
       let api = "";
       let API_KEY = 'a612a032fc214df2962a6fcd6d4fe944'
       if (location.pathname === "/news/home") {
         api = `https://newsapi.org/v2/top-headlines?country=in&apiKey=${API_KEY}`;
       } 
       else if (location.pathname === "/news/Science") {
         console.log("hh");
         api = `https://newsapi.org/v2/top-headlines/sources?category=science&apiKey=${API_KEY }`;
       }
       else if (location.pathname === "/news/Sport") {
         api = `https://newsapi.org/v2/top-headlines/sources?category=sports&apiKey=${API_KEY }`;
       }
       else if (location.pathname === "/news/Business") {
         api = 'https://newsapi.org/v2/top-headlines/sources?category=business&apiKey=a612a032fc214df2962a6fcd6d4fe944';
       }
       else if (location.pathname === "/news/Entertainment") {
         api = `https://newsapi.org/v2/top-headlines/sources?category=entertainment&apiKey=${API_KEY }`;
       }
       else  {
         api = `https://newsapi.org/v2/top-headlines/sources?category=technologyapi&Key=${API_KEY }`;
       }

       await fetch(api)
         .then((response) => response.json())
         .then((val) => {
           const articlesWithIds = val.articles.map((article, index) => ({
             ...article,
             id: index + 1,
           }));
           setData(articlesWithIds);
         });
     };

     fetchData();
   }, [location.pathname]);
console.log(data);
   return (
     <div>
       <NewsHome data={data} />
     </div>
   );
 };

 export default NewsItem;
