import React, { useEffect, useState } from "react";
import NewsHome from "./NewsHome";
import { useNavigate } from "react-router-dom"; // Import useNavigate

const NewsItem = () => {
  const [data, setData] = useState([]);
  const [isLoading, setIsLoading] = useState(false);
  const navigate = useNavigate(); // Get the navigate function

  useEffect(() => {
    const getData = async () => {
      setIsLoading(true);
      await fetch(
        "https://newsapi.org/v2/top-headlines?country=in&apiKey=a612a032fc214df2962a6fcd6d4fe944"
      )
        .then((response) => response.json())
        .then((val) => {
          const articlesWithIds = val.articles.map((article, index) => ({
            ...article,
            id: index + 1,
          }));
          setTimeout(() => {
            setData(articlesWithIds);
            setIsLoading(false);
          }, 1000);
        });
    };
    getData();
  }, []);

  // Function to navigate to the Post component with data
  const navigateToPost = (postId) => {
    navigate(`/news/post/${postId}`, { state: { data } }); // Pass data as state
  };

  return (
    <div>
      <NewsHome data={data} loading={isLoading} />
      {/* Render the cards and handle clicks */}
      {data.map((item) => (
        <div className="card" key={item.id} onClick={() => navigateToPost(item.id)}>
          {/* Render card content here */}
        </div>
      ))}
    </div>
  );
};

export default NewsItem;
