import React, { useEffect, useState } from "react";
import { useLocation } from "react-router-dom";
import NewsHome from "./NewsHome";

const NewsItem = () => {
  const location = useLocation();
  const [data, setData] = useState([]);

  useEffect(() => {
    const fetchData = async () => {
      let api = "";

      if (location.pathname === "/news/home") {
        api = "https://newsapi.org/v2/top-headlines?country=in&category=science&apiKey=a612a032fc214df2962a6fcd6d4fe944";
      } 
      else if (location.pathname === "/news/Science") {
        api = "https://newsapi.org/v2/top-headlines?country=in&category=science&apiKey=a612a032fc214df2962a6fcd6d4fe944";
      }
      else if (location.pathname === "/news/Sport") {
        api = "https://newsapi.org/v2/top-headlines?country=in&category=science&apiKey=a612a032fc214df2962a6fcd6d4fe944";
      }
      else if (location.pathname === "/news/Business") {
        api = "https://newsapi.org/v2/top-headlines?country=in&category=science&apiKey=a612a032fc214df2962a6fcd6d4fe944";
      }
      else if (location.pathname === "/news/Enterntaiment") {
        api = "https://newsapi.org/v2/top-headlines?country=in&category=science&apiKey=a612a032fc214df2962a6fcd6d4fe944";
      }
      else  {
        api = "https://newsapi.org/v2/top-headlines?country=in&category=science&apiKey=a612a032fc214df2962a6fcd6d4fe944";
      }

      await fetch(api)
        .then((response) => response.json())
        .then((val) => {
          const articlesWithIds = val.articles.map((article, index) => ({
            ...article,
            id: index + 1,
          }));
          setData(articlesWithIds);
        });
    };

    fetchData();
  }, [location.pathname]);

  return (
    <div>
      <NewsHome data={data} />
    </div>
  );
};

export default NewsItem;
