import React, { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";

const NewsItem = () => {
  const [data, setData] = useState([]);
  const [isLoading, setIsLoading] = useState(false);
  const navigate = useNavigate();

  useEffect(() => {
    const getData = async () => {
      setIsLoading(true);
      await fetch(
        "https://newsapi.org/v2/top-headlines?country=in&apiKey=a612a032fc214df2962a6fcd6d4fe944"
      )
        .then((response) => response.json())
        .then((val) => {
          const articlesWithIds = val.articles.map((article, index) => ({
            ...article,
            id: index + 1,
          }));
          setTimeout(() => {
            setData(articlesWithIds);
            setIsLoading(false);
          }, 1000);
        });
    };
    getData();
  }, []);

  const navigateToPost = (postId) => {
    navigate(`/news/post/${postId}`, { state: { data } });
  };

  return (
    <div>
      {/* Render the cards and handle clicks */}
      {data.map((item) => (
        <div
          className="card"
          key={item.id}
          onClick={() => navigateToPost(item.id)}
        >
          <span className='title'>{item.author}</span>
          <img src={item.urlToImage} alt="" className='img' />
          <p className='desc'>{item.description}</p>
        </div>
      ))}
    </div>
  );
};

export default NewsItem;
