import React, { useEffect, useState } from "react";
import { useLocation } from "react-router-dom";
import NewsHome from "./NewsHome";

const NewsItem = () => {
  const location = useLocation();
  const [data, setData] = useState([]);

  useEffect(() => {
    const fetchNewsData = async () => {
      let category = "general"; // Default category

      // Extract the category from the pathname
      const pathname = location.pathname.toLowerCase();
      if (pathname.includes("science")) {
        category = "science";
      } else if (pathname.includes("sport")) {
        category = "sport";
      } else if (pathname.includes("business")) {
        category = "business";
      } else if (pathname.includes("entertainment")) {
        category = "entertainment";
      } else if (pathname.includes("culture")) {
        category = "culture";
      }

      const apiKey = "YOUR_API_KEY"; // Replace with your News API key
      const apiUrl = `https://newsapi.org/v2/top-headlines?country=in&category=${category}&apiKey=${apiKey}`;

      try {
        const response = await fetch(apiUrl);
        if (response.ok) {
          const data = await response.json();
          const articlesWithIds = data.articles.map((article, index) => ({
            ...article,
            id: index + 1,
          }));
          setData(articlesWithIds);
        } else {
          console.error("Failed to fetch news data:", response.status);
        }
      } catch (error) {
        console.error("Error fetching news data:", error);
      }
    };

    fetchNewsData();
  }, [location.pathname]);

  return (
    <div>
      <NewsHome data={data} />
    </div>
  );
};

export default NewsItem;
