import React, { useEffect, useState } from "react";
import NewsHome from "./NewsHome";
import Card from "../components/Card"; 
import { useNavigate } from "react-router-dom"; 

const NewsList = () => {
  const [data, setData] = useState([]);
  const [isLoading, setIsLoading] = useState(false);
  const navigate = useNavigate(); 

  useEffect(() => {
    const getData = async () => {
      setIsLoading(true);
      await fetch(
        "https://newsapi.org/v2/top-headlines?country=in&apiKey=a612a032fc214df2962a6fcd6d4fe944"
      )
        .then((response) => response.json())
        .then((val) => {
          const articlesWithIds = val.articles.map((article, index) => ({
            ...article,
            id: index + 1,
          }));
          setTimeout(() => {
            setData(articlesWithIds);
            setIsLoading(false);
          }, 1000);
        });
    };
    getData();
  }, []);

  // Function to navigate to the Post component with data
  const navigateToPost = (postId) => {
    navigate(`/news/post/${postId}`, { state: { data } });
  };

  return (
    <div>
      <NewsHome data={data} loading={isLoading} />
      {/* Render the cards and handle clicks */}
      {data.map((item) => (
        <Card key={item.id} item={item} onClick={() => navigateToPost(item.id)} />
      ))}
    </div>
  );
};

export default NewsList;
