import React, { useEffect, useState } from 'react';
import axios from 'axios';
import Home from './Home';

const NewsItem = () => {
  const [data, setData] = useState([]);

  const fetchData = async () => {
    try {
      const response = await axios.get('https://newsapi.org/v2/everything?q=tesla&from=2023-08-14&sortBy=publishedAt&apiKey=a612a032fc214df2962a6fcd6d4fe944');
      setData(response.data.articles);
      console.log(response.data.articles);
    } catch (error) {
      console.error('Error fetching data:', error);
    }
  };

  useEffect(() => {
    // Throttle requests by introducing a delay (e.g., 2 seconds) between requests
    const delay = 2000; // 2 seconds
    const timer = setInterval(fetchData, delay);

    // Fetch data immediately when the component mounts
    fetchData();

    return () => {
      // Clear the interval when the component unmounts
      clearInterval(timer);
    };
  }, []);

  return (
    <div>
      {/* Pass the 'data' array as a prop to the 'Home' component */}
    </div>
  );
};

export default NewsItem;
