import React from 'react';
import '../css/Card.css';
import { useHistory } from 'react-router-dom';

const Card = ({ item }) => {
  const history = useHistory();

  // Function to handle the click event and redirect to another component
  const redirectToNews = () => {
    // Replace '/news' with the desired route to which you want to redirect
    history.push('/news');
  };

  return (
    <div className='card' onClick={redirectToNews}>
      <span className='title'>{item.author}</span>
      <img src={item.urlToImage} alt="" className='img' />
      <p className='desc'>{item.description}</p>
    </div>
  );  
};

export default Card;
