import React, { useEffect, useState } from 'react';
import NewsHome from '../components/NewsHome';

const Home = ({ user }) => {
  const [data, setData] = useState([]);
  const [isLoading, setIsLoading] = useState(false);

  useEffect(() => {
    const getData = async () => {
      setIsLoading(true);
      await fetch(
        "https://newsapi.org/v2/top-headlines?country=in&apiKey=a612a032fc214df2962a6fcd6d4fe944"
      )
        .then((response) => response.json())
        .then((val) => {
          const articlesWithIds = val.articles.map((article, index) => ({
            id: `${index + 1}`,
            title: article.title,
            img: article.urlToImage,
            longDesc: article.description,
          }));
          setData(articlesWithIds);
          setIsLoading(false);
        });
    };
    getData();
  }, []);

  return (
    <div>
      <NewsHome data={data} loading={isLoading} user={user} />
    </div>
  );
};

export default Home;
