import React from 'react';
import '../css/post.css';
import { useLocation, useParams } from 'react-router-dom'; // Import useParams

const Post = ({ data }) => {
  const { id } = useParams(); // Get the post ID from the route params
  const location = useLocation();
  const path = location.pathname.split('/')[2];
  const post = data.find((item) => item.id === parseInt(id)); // Find the post by ID

  if (!post) {
    // Handle the case where the post is not found
    return (
      <div className="post">
        <p>Post not found.</p>
      </div>
    );
  }

  return (
    <div className='post'>
      <img src={post.urlToImage} alt="" className='img' />
      <span className='title'>{post.title}</span>
      <p className='longdesc'>{post.description}</p>
    </div>
  );
};

export default Post;
